// auxiliary functions for visualization
// these won't show up if they are moved to a different file so they have to live here
fun Live : set PMObj {
    { o: PMObj | live[o] }
}
// orphan objects are all objects NOT reachable from the root
fun Orphan : set PMObj {
    { o: PMObj | orphan[o] }
}

fun start_alloc_file_inode : Event->Inode {
    { e: StartAllocInode, i: FileInode | start_alloc_inode[i] }
}

fun start_alloc_dir_inode : Event->Inode {
    { e: StartAllocInode, i: DirInode | start_alloc_inode[i] }
}

fun set_inode_ino : Event->Inode {
