    // set_inode_ctime.Inode +
    finish_alloc_inode.Inode +
    dentry_set_name_bytes.Dentry +
    // dentry_set_partial_name.Dentry +
    // dentry_set_full_name.Dentry +
    set_ino_in_dentry.Dentry.Inode +
    complete_create.Dentry.Inode +
    start_alloc_dir_page.DirPageHeader + 
    set_page_header_type.PageHeader +
    set_dir_page_parent.DirInode.DirPageHeader +
    finish_alloc_dir_page.DirPageHeader +
    set_page_backpointer.Inode.PageHeader +
    inc_link_count.Inode + 
    clwb.PMObj +
    fence +
    skip
}

fact transition {
    // always some events
    always one events
}

// some constraints to reduce frame condition verbosity
// these ensure that certain sets can only be modified in a step that involves certain operations
// e.g., an inode can only be added or removed from InodeSet by an operation that updates 
// an inode's ino number.
// TODO: we may have some redundant frame condition constraints in transitions; see if we can 
// take any of them out without breaking anything
// see https://alloytools.discourse.group/t/how-to-reduce-temporal-update-boilerplate-for-state-rich-models/219/2
fact { always (InodeSet' != InodeSet => some set_inode_ino.Inode) }
fact { always (LcSet' != LcSet => (some set_dir_inode_link_count.Inode or some set_file_inode_link_count.Inode)) }
fact { always (MetadataSet' != MetadataSet => some set_inode_metadata.Inode) }
// fact { always (FlagsSet' != FlagsSet => some set_inode_flags.Inode) }
// fact { always (ModeSet' != ModeSet => some set_inode_mode.Inode) }
// fact { always (UidSet' != UidSet => some set_inode_uid.Inode) }
// fact { always (GidSet' != GidSet => some set_inode_gid.Inode) }
// fact { always (RDevSet' != RDevSet => some set_inode_rdev.Inode) }
// fact { always (ATimeSet' != ATimeSet => some set_inode_atime.Inode) }
// fact { always (MTimeSet' != MTimeSet => some set_inode_mtime.Inode) }
// fact { always (CTimeSet' != CTimeSet => some set_inode_ctime.Inode) }
fact { always (all d: Dentry | (d.(name_qw_set') != d.name_qw_set) => (some dentry_set_name_bytes)) }
// fact { always (PartialName' != PartialName => (some dentry_set_partial_name.Dentry or some dentry_set_full_name.Dentry)) }
// fact { always (FullName' != FullName => some dentry_set_full_name.Dentry) }
fact { always (TypeSet' != TypeSet => some set_page_header_type.PageHeader) }
fact { always (all i: Inode | (i.(link_count') != i.link_count) => (some set_dir_inode_link_count.Inode or some set_file_inode_link_count.Inode or some inc_link_count.Inode))}


// checks and assertions

// check that it's actually possible to fully run creat
// says that the model eventually reaches a point where d and i are in Start state,
// AND, when in that state, it is the case that d was previously in init and i was previously in alloc
// this should return an instance 
pred create_file {
    some d: Dentry, i: FileInode | d in Live and i in Orphan and
                                    eventually (d in Start and i in Start) and 
                                    ((d in Start and i in Start) => 
                                        once (d in Init and i in Complete))
}
run create_file { 
    create_file
} for 1 Volatile, 5 PMObj, 1..25 steps

// inodes in start or complete state should always be clean
start_complete_clean: check {
    always (all i: Inode | (i in Start or i in Complete) => i in Clean)
} for 1 Volatile, 5 PMObj, 1..25 steps


run mkdir {
    some p: DirPageHeader, new_i, parent_i: DirInode, d: Dentry {
        eventually (p in Init and d in Init and new_i in Start and parent_i in Start)
    }
} for 1 Volatile, 5 PMObj, 1..25 steps

mkdir_link_count_check: check {
    some p: DirPageHeader, new_i, parent_i: DirInode, d: Dentry {
        (new_i in Free and (eventually (p in Init and d in Init and new_i in Start and parent_i in Start))) =>
        eventually (
            parent_i.(link_count') = add[parent_i.link_count, 1] and // at some point p's link count increases
            eventually ( // and at some later point
                parent_i.(Volatile.(children')) = parent_i.(Volatile.children) + new_i // p obtains new as a child
            )
        )
    }
} for 1 Volatile, 5 PMObj, 1..25 steps

link_count: check {
    always (
        (all i: FileInode {
            initialized[i] => gte[i.link_count, #i.linked_dentries]
        }) and 
        (all i: DirInode {
            initialized[i] => gte[i.link_count, add[2, #i.child_directories]]
        }) 
    )
} for 1 Volatile, 10 PMObj, 1..25 steps

// dentries cannot point to uninit inodes
point_to_uninit0: check {
    always (all d: Dentry, i: Inode | !initialized[i] => i !in d.inode)
} for 1 Volatile, 3 Inode, 3 Dentry, 3 PageHeader, 1..20 steps

// page headers cannot point to uninit inodes
point_to_uninit1: check {
    always (all p: PageHeader, i: Inode | !initialized[i] => i !in p.inode)
} for 1 Volatile, 3 Inode, 3 Dentry, 3 PageHeader, 1..20 steps

// uninit inodes cannot have parents
// sanity check - should be implied directly by the definition of parent
// children is the inverse of parent so we don't need to check it
point_to_uninit2: check {
    always (all v: Volatile, i: Inode, dir: DirInode | !initialized[i] => dir !in i.(v.parent))
} for 1 Volatile, 3 Inode, 3 Dentry, 3 PageHeader, 1..20 steps

// uninit pages cannot be owned by inodes
// sanity check - should be implied directly by the definition of owns
point_to_uninit3: check {
    always (all v: Volatile, i: Inode, p: PageHeader | !initialized[p] => p !in i.(v.owns))
} for 1 Volatile, 3 Inode, 3 Dentry, 3 PageHeader, 1..20 steps

// TODO: should this actually be illegal? I don't think it breaks any soft updates rules
// // a free dir page cannot contain allocated/initialized dentries
// point_to_uninit4: check {
//     always (all p: DirPageHeader, d: p.dentries | p in Free => d in Free)
// } for 1..25 steps

// if an inode is live, it should be fully allocated
live_inode_allocated: check {
    always (all i: Inode | i in Live => inode_alloc_done[i] )
} for 1 Volatile, 5 PMObj, 1..25 steps

// initialized dentries (i.e., not free or in AllocStarted) should always have a full name
init_dentry_full_name: check {
    // always (all d: Dentry | initialized[d] => d in FullName)
    always (all d:Dentry | initialized[d] => eq[d.name_qw_set, 2])
} for 1 Volatile, 5 PMObj, 1..25 steps

// test frame conditions

pred i_change_j_unchanged [i, j: Inode, s: InodeValues] {
    // if i and j are distinct and i moved in or out of s, then j should stay where it is
    ((i != j) and ((i !in s and i in s') or (i in s and i !in s'))) => (
        j in s => 
            j in s'
        else 
            j !in s'
    )
}
// for two distinct inodes i and j, if one of i's values changes in one step, then none of j's values
// should change in that step
inode_fc_single_ino: check {
    always ( 
        (all i, j: Inode | i != j => i_change_j_unchanged[i, j, InodeSet]) and 
        (all i, j: Inode | i != j => i_change_j_unchanged[i, j, LcSet]) and 
        (all i, j: Inode | i != j => i_change_j_unchanged[i, j, MetadataSet])
        // (all i, j: Inode | i != j => i_change_j_unchanged[i, j, FlagsSet] ) and
        // (all i, j: Inode | i != j => i_change_j_unchanged[i, j, ModeSet] ) and
        // (all i, j: Inode | i != j => i_change_j_unchanged[i, j, UidSet] ) and 
        // (all i, j: Inode | i != j => i_change_j_unchanged[i, j, GidSet] )
    )
} for 1 Volatile, 5 PMObj, 1..20 steps

// check that only one set changes at a time
inode_fc_single_field: check {
    always (
        (InodeSet != InodeSet' => inode_values_unchanged_except[InodeSet]) and 
        (LcSet != LcSet' => inode_values_unchanged_except[LcSet]) and 
        (MetadataSet != MetadataSet' => inode_values_unchanged_except[MetadataSet])
        // (FlagsSet != FlagsSet' => inode_values_unchanged_except[FlagsSet]) and 
        // (ModeSet != ModeSet' => inode_values_unchanged_except[ModeSet]) and 
        // (UidSet != UidSet' => inode_values_unchanged_except[UidSet]) and 
        // (GidSet != GidSet' => inode_values_unchanged_except[GidSet]) 
    )

} for 1 Volatile, 5 PMObj, 1..20 steps

module util/integer

/*
 * A collection of utility functions for using Integers in Alloy.
 * Note that integer overflows are silently truncated to the current bitwidth
 * using the 2's complement arithmetic, unless the "forbid overfows" option is
 * turned on, in which case only models that don't have any overflows are 
 * analyzed. 
 */

fun add  [n1, n2: Int] : Int { this/plus[n1, n2] }
fun plus [n1, n2: Int] : Int { n1 fun/add n2 }

fun sub   [n1, n2: Int] : Int { this/minus[n1, n2] }
fun minus [n1, n2: Int] : Int { n1 fun/sub n2 }

fun mul [n1, n2: Int] : Int { n1 fun/mul n2 }

/**
 * Performs the division with "round to zero" semantics, except the following 3 cases
 * 1) if a is 0, then it returns 0
 * 2) else if b is 0, then it returns 1 if a is negative and -1 if a is positive
 * 3) else if a is the smallest negative integer, and b is -1, then it returns a
 */
fun div [n1, n2: Int] : Int { n1 fun/div n2 }

/** answer is defined to be the unique integer that satisfies "a = ((a/b)*b) + remainder" */
fun rem [n1, n2: Int] : Int { n1 fun/rem n2 }

/** negate */
fun negate [n: Int] : Int { 0 fun/sub n }

/** equal to */
pred eq [n1, n2: Int] { int[n1] = int[n2] }

/** greater than */
pred gt [n1, n2: Int] { n1 > n2 }

/** less then */
pred lt [n1, n2: Int] { n1 < n2 }

/** greater than or equal */
pred gte [n1, n2: Int] { n1 >= n2 }

/** less than or equal */
pred lte [n1, n2: Int] { n1 <= n2 }

/** integer is zero */
pred zero [n: Int] { n = 0 }

/** positive */
pred pos  [n: Int] { n > 0 }

/** negative */
pred neg  [n: Int] { n < 0 }

/** non-positive */
pred nonpos [n: Int] { n <= 0 }

/** non-negative */
pred nonneg [n: Int] { n >= 0 }

/** signum (aka sign or sgn) */
fun signum [n: Int] : Int { n<0 => (0 fun/sub 1) else (n>0 => 1 else 0) }

/**
 * returns the ith element (zero-based) from the set s
 * in the ordering of 'next', which is a linear ordering
 * relation like that provided by util/ordering
 */
fun int2elem[i: Int, next: univ->univ, s: set univ] : lone s {
  {e: s | #^next.e = int i }
}

/**
 * returns the index of the element (zero-based) in the
 * ordering of next, which is a linear ordering relation
 * like that provided by util/ordering
 */
fun elem2int[e: univ, next: univ->univ] : lone Int {
  Int[#^next.e]
}

/** returns the largest integer in the current bitwidth */
fun max:one Int { fun/max }

/** returns the smallest integer in the current bitwidth */
fun min:one Int { fun/min }

/** maps each integer (except max) to the integer after it */
fun next:Int->Int { fun/next }

/** maps each integer (except min) to the integer before it */
fun prev:Int->Int { ~next }

/** given a set of integers, return the largest element */
fun max [es: set Int]: lone Int { es - es.^prev }

/** given a set of integers, return the smallest element */
fun min [es: set Int]: lone Int { es - es.^next }

/** given an integer, return all integers prior to it */
fun prevs [e: Int]: set Int { e.^prev }

/** given an integer, return all integers following it */
fun nexts [e: Int]: set Int { e.^next }

/** returns the larger of the two integers */
fun larger [e1, e2: Int]: Int { let a=int[e1], b=int[e2] | (a<b => b else a) }

/** returns the smaller of the two integers */
fun smaller [e1, e2: Int]: Int { let a=int[e1], b=int[e2] | (a<b => a else b) }

open util/integer

// macros for managing frame conditions
let unchanged[x] = { (x') = (x) }
let pointers_unchanged = {
    all d: Dentry | unchanged[d.inode]
    all p: PageHeader | unchanged[p.inode]
    all p: DirPageHeader | (unchanged[p.dentries] and unchanged[p.parent_ino])
}
let pm_states_unchanged = {
    unchanged[Clean]
    unchanged[InFlight]
    unchanged[Dirty]
}
let make_dirty[x] = {
    Dirty' = Dirty + x
    Clean' = Clean - x
    InFlight' = InFlight - x
}
let op_states_unchanged = {
    unchanged[Start]
    unchanged[Free]
    unchanged[AllocStarted]
    unchanged[Alloc]
    unchanged[Init]
    unchanged[IncLink]
    unchanged[Complete]
}
let start_alloc_frame_condition = {
    op_states_unchanged_except[Free, AllocStarted]
}
let finish_alloc_frame_condition = {
    op_states_unchanged_except[AllocStarted, Alloc]
}
let init_frame_condition = {
    op_states_unchanged_except[Alloc, Init]
}

// persistent objects
abstract sig PMObj {}
some sig Dentry extends PMObj {
    var inode: lone Inode,
    var name_qw_set: lone Int
}
abstract sig Inode extends PMObj {
    var link_count: lone Int
} {
    always (gte[link_count, 0])
}
sig DirInode extends Inode {}
sig FileInode extends Inode {}
abstract sig PageHeader extends PMObj {
    var inode: lone Inode
}
sig DataPageHeader extends PageHeader {}
sig DirPageHeader extends PageHeader {
    var parent_ino: lone Inode,
    var dentries: set Dentry
}

// if an inode is in one of these sets, the corresponding value has been set in the 
// inode (but it may or may not be persistent)
var sig InodeValues in Inode {}
// MetadataSet represents all metadata not covered by the other sets
var sig InodeSet, LcSet, MetadataSet in InodeValues {}
// these are not used right now because we do not model updates to them and do not really care what their values are
// FlagsSet, ModeSet, UidSet, GidSet, RDevSet, CTimeSet, ATimeSet, MTimeSet in InodeValues {}

// // if a dentry is in one of these sets, its name is partially or fully set (but may
// // or may not be persistent)
// var sig DentryName in Dentry {}
// var sig PartialName, FullName in DentryName {}

// represents whether the type field of a page header has been set
// other page header fields are represented as fields in their sigs
var sig TypeSet in PageHeader {}

one sig Root extends DirInode {}

// persistence states
var sig Dirty, InFlight, Clean in PMObj {}
// operation states
var sig Start, Free, AllocStarted, Alloc, Init, Complete, IncLink in PMObj {}

// general constraints

// the root inode is not pointed to by any dentries
fact {
    always (all d: Dentry | d.inode != Root)
}
// DirPageHeaders only point to DirInodes
fact {
    always (all p: DirPageHeader, i: Inode | i in p.inode => i in DirInode)
}
// DataPageHeaders only point to FileInodes
fact {
    always (all p: DataPageHeader, i: FileInode | i in p.inode => i in FileInode)
}
// each object can only be in one persistence state at a time
fact {
    always (disj[Dirty, InFlight, Clean])
}
// each object can only be in one op state at a time
fact {
    always (disj[Start, Free, AllocStarted, Alloc, Init, Complete, IncLink])
} 
// dentries always must belong to one page header
fact {
    always(all d: Dentry | one p: DirPageHeader | d in p.dentries )
}

// TODO: can we make this stuff auxiliary relations rather than its own Volatile thingy?
// might be cleanear in the visualization, but also might be harder to model in code
one sig Volatile {
    var parent: Inode -> lone DirInode, // TODO: need to adjust for hard links
    var children: DirInode lone -> Inode, // TODO: need to adjust for hard links
    var owns: Inode lone -> set PageHeader,
} {
    always (no Root.parent)

    // defining owns relation - i owns p if p points to i
    always (all i: Inode, p: PageHeader | i in p.inode <=> p in i.owns)

    // i is j's parent if j is pointed to by a dentry in i
    always (all i: DirInode, j: Inode | i in j.parent <=> j in i.owns.dentries.inode)

    // i cannot be an ancestor of its own parent 
    always (all i: Inode | i !in i.^parent)

    // children relation is inverse of parent relation
    always (children = ~parent)
}

fun dentry_parent : Dentry -> Inode {
    { d: Dentry, i: Inode | d in i.(Volatile.owns).dentries}
}

fun linked_dentries : Inode -> Dentry {
    { i: Inode, d: Dentry | i in d.inode }
}

fun child_directories : Inode -> set DirInode {
    { i: Inode, d: DirInode | d in i.(Volatile.children) }
}

fact link_count_set {
    always (all i: Inode | some i.link_count <=> i in LcSet)
}


// initial state
fact init {
    no Dirty and 
    no InFlight and 
    Root in Start and
    (all o: PMObj | o in Clean) and 
    (all o: PMObj | o in Free or o in Start) and
    (all d: Dentry | d in Free => no d.inode) and
    (all p: PageHeader | p in Free => no p.inode) and
    (all p: PageHeader, i: Inode | i in Free => i !in p.inode) and
    (all p: DirPageHeader, d: p.dentries | p in Free => d in Free) and
    (all p: DirPageHeader | p in Free => no p.parent_ino) and
    (all i: Inode | i !in Free => inode_alloc_done[i]) and 
    (all i: Inode | i in Free => inode_clear[i]) and 
    initial_link_count and
    (all d: Dentry | d !in Free => dentry_alloc_done[d]) and 
    (all d: Dentry | d in Free => dentry_clear[d]) and 
    (all d: Dentry, i: Inode | i in Free => i !in d.inode) and 
    (all p: DataPageHeader | p in Free => data_page_header_clear[p]) and
    (all p: DirPageHeader | p in Free => dir_page_header_clear[p]) and 
    (all o: PMObj | orphan[o] => o in Free)
}

pred initial_link_count {
    (all i: FileInode {
        initialized[i] =>
            i.link_count = #i.linked_dentries
        else 
            no i.link_count
    }) and
    (all i: DirInode {
        initialized[i] =>
            i.link_count = add[2, #i.child_directories]
        else 
            no i.link_count
    })
}

pred live [o: PMObj] {
    o in Root.*(Volatile.children) + 
        Root.*(Volatile.owns) +
        Root.*(Volatile.owns.dentries) +
        Root.*(Volatile.children.(Volatile.owns)) + 
        Root.*(Volatile.children.(Volatile.owns.dentries))
}

pred orphan [o: PMObj] {
    !live[o]
}

// returns true if the PMObj is initialized
pred initialized [o: PMObj] {
	o in Inode =>
		o !in Free and o !in AllocStarted
	else 
		o !in Free and o !in Alloc and o !in AllocStarted
}

pred inode_alloc_done [i: Inode] {
    i in InodeSet and i in LcSet and i in MetadataSet
    // and i in FlagsSet and i in ModeSet and
    // i in GidSet and i in UidSet and i in RDevSet and i in ATimeSet and
    // i in CTimeSet and i in MTimeSet
}

pred inode_clear [i: Inode] {
    i !in InodeSet and i !in LcSet and i in MetadataSet
    // and i !in FlagsSet and i !in ModeSet and
    // i !in GidSet and i !in UidSet and i !in RDevSet and i !in ATimeSet and 
    // i !in CTimeSet and i !in MTimeSet
}

pred inode_values_unchanged_except [s: InodeValues] {
    (s != InodeSet => unchanged[InodeSet]) and 
    (s != LcSet => (unchanged[LcSet] and (all i: Inode | unchanged[i.link_count]))) and
    (s != MetadataSet => unchanged[MetadataSet])
    // (s != FlagsSet => unchanged[FlagsSet]) and 
    // (s != ModeSet => unchanged[ModeSet]) and 
    // (s != UidSet => unchanged[UidSet]) and 
    // (s != GidSet => unchanged[GidSet]) and 
    // (s != RDevSet => unchanged[RDevSet]) and 
    // (s != ATimeSet => unchanged[ATimeSet]) and 
    // (s != CTimeSet => unchanged[CTimeSet]) and 
    // (s != MTimeSet => unchanged[MTimeSet])
}

pred op_states_unchanged_except [s0, s1: PMObj] {
    ((s0 != Start and s1 != Start) => unchanged[Start]) and
    ((s0 != Free and s1 != Free) => unchanged[Free]) and
    ((s0 != AllocStarted and s1 != AllocStarted) => unchanged[AllocStarted]) and
    ((s0 != Alloc and s1 != Alloc) => unchanged[Alloc]) and
    ((s0 != Init and s1 != Init) => unchanged[Init]) and
    ((s0 != IncLink and s1 != IncLink) => unchanged[IncLink]) and 
    ((s0 != Complete and s1 != Complete) => unchanged[Complete])
}

pred inode_values_unchanged {
    unchanged[InodeSet] and unchanged[LcSet] and unchanged[MetadataSet]
    // and unchanged[FlagsSet] and unchanged[ModeSet] and
    // unchanged[UidSet] and unchanged[GidSet] and unchanged[RDevSet] and unchanged[ATimeSet] and 
    // unchanged[CTimeSet] and unchanged[MTimeSet] and (all i: Inode | unchanged[i.link_count])
}

pred dentry_alloc_done [d: Dentry] {
    // d in FullName
    eq[d.name_qw_set, 2]
}

pred dentry_clear [d: Dentry] {
    // d !in FullName and d !in PartialName 
    eq[d.name_qw_set, 0] and no d.inode
}

pred data_page_header_alloc_done [p: DataPageHeader] {
    p in TypeSet
}

pred dir_page_header_alloc_done [p: DirPageHeader] {
    p in TypeSet and some p.parent_ino
}

pred data_page_header_clear [p: DataPageHeader] {
    p !in TypeSet and no p.inode
}

pred dir_page_header_clear [p: DirPageHeader] {
    p !in TypeSet and no p.inode and no p.parent_ino
}

// TODO: this needs some work. it should express that during mkdir, there is always one parent inode 
// that will both obtain a new child and have its link count incremented. this SHOULD ensure that 
// both of these things happen for ONE parent per mkdir operation.
// but we don't have a great way of representing the actual link count for a given inode right now,
// just whether it has been set during allocation or not
fact mkdir_parent {
    always (
        all v: Volatile, i: DirInode | (orphan[i] and eventually live[i]) => 
            (one p: DirInode | eventually (p in i.(v.parent)))
    )
}

