pred start_alloc_inode [i: Inode] {
    // guard
    i in Clean and orphan[i] and i in Free 

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    start_alloc_frame_condition
    pm_states_unchanged

    // effects
    AllocStarted' = AllocStarted + i
    Free' = Free - i
}

pred set_inode_ino [i: Inode] {
    // guard
    i in AllocStarted and i !in InodeSet

    // frame conditions
    inode_values_unchanged_except[InodeSet]
    pointers_unchanged
    op_states_unchanged

    // effects
    make_dirty[i]
    InodeSet' = InodeSet + i
}

// TODO: should this be used to manage link count for link/unlink
// or just for allocation?
pred set_dir_inode_link_count [i: DirInode] {
    // guard
    i in AllocStarted and i !in LcSet

    // frame conditions
    inode_values_unchanged_except[LcSet]
    pointers_unchanged
    op_states_unchanged
    all i0: Inode - i | unchanged[i0.link_count]

    // effects
    make_dirty[i]
    i.link_count' = 2
    // LcSet should be automatically updated
    // LcSet' = LcSet + i
}

pred set_file_inode_link_count [i: FileInode] {
    // guard 
    i in AllocStarted and i !in LcSet 

    // frame conditions
    inode_values_unchanged_except[LcSet]
    pointers_unchanged
    op_states_unchanged
    all i0: Inode - i | unchanged[i0.link_count]

    // effects 
    make_dirty[i]
    i.link_count' = 1
}

