pred set_inode_metadata [i: Inode] {
    // guard 
    i in AllocStarted and i !in MetadataSet 

    // frame conditions
    inode_values_unchanged_except[MetadataSet]
    pointers_unchanged
    op_states_unchanged

    // effects 
    make_dirty[i]
    MetadataSet' = MetadataSet + i
}

pred finish_alloc_inode [i: Inode] {
    // guard
    i in AllocStarted and inode_alloc_done[i]

    // frame conditions
    pointers_unchanged 
    inode_values_unchanged
    finish_alloc_frame_condition
    pm_states_unchanged

    AllocStarted' = AllocStarted - i
    Alloc' = Alloc + i
}

// TODO: should the page containing the dentry be live? I don't think it is 
// crash-unsafe to allocate dentries from orphaned pages, but it is 
// not very useful and we will not (or should not) do it in practice
// this operation begins dentry allocation
// pred dentry_set_partial_name [d: Dentry] {
//     // guard 
//     d in Clean and d in Free 

//     // frame conditions
//     pointers_unchanged
//     inode_values_unchanged
//     start_alloc_frame_condition

//     // effects
//     make_dirty[d]
//     Free' = Free - d
//     AllocStarted' = AllocStarted + d
//     PartialName' = PartialName + d
// }

// pred dentry_set_full_name [d: Dentry] {
//     // guard 
//     d in AllocStarted and d in PartialName 

//     // frame conditions
//     pointers_unchanged
//     inode_values_unchanged
//     finish_alloc_frame_condition

//     // effects
//     make_dirty[d] // the dentry may have been flushed/fenced since we set the partial name
//     AllocStarted' = AllocStarted - d
//     Alloc' = Alloc + d 
//     PartialName' = PartialName - d
//     FullName' = FullName + d
// }

// file names can be at most 16 bytes - this is hard coded into this transaction right now
// this transition sets some bytes in the name by increasing the number of quadwords
// that have been set in the name. the specific bytes that are set or clear does 
// not really matter for the purposes of the model
pred dentry_set_name_bytes [d: Dentry] {
    // guard 
    (d in AllocStarted and eq[d.name_qw_set, 1]) or d in Free 

    // frame conditions
    pointers_unchanged
    inode_values_unchanged 
    all d0: Dentry - d | unchanged[d0.name_qw_set]

    // effects 
    make_dirty[d]
    d in Free => (
        start_alloc_frame_condition and 
        AllocStarted' = AllocStarted + d and 
        Free' = Free - d
    )
    d in AllocStarted => (
        finish_alloc_frame_condition and 
        AllocStarted' = AllocStarted - d and 
        Alloc' = Alloc + d
    )

    d.(name_qw_set') = add[d.name_qw_set, 1]
}

pred set_ino_in_dentry [i: Inode, d: Dentry] {
    // guards
    d in Clean and d in Alloc and 
    (i in IncLink or i in Alloc) and i in Clean and
    // if the inode is a directory, parent must have incremented link count
    (i in DirInode => (d.dentry_parent in IncLink and d.dentry_parent in Clean)) 


    // frame conditions
    all d1: Dentry - d | unchanged[d1.inode]
    all p: PageHeader | unchanged[p.inode]
    all p: DirPageHeader | unchanged[p.dentries]
    inode_values_unchanged
    unchanged[InFlight]
    unchanged[AllocStarted]
    unchanged[Free]

    // modifying d makes it dirty 
    Dirty' = Dirty + d
    Clean' = Clean - d 

    // d's inode is now i
    d.(inode') = i 
    // move i to the start state
    // and if i is a directory entry, also move parent to the start state
    // otherwise, don't touch the parent
    i in DirInode => (
        let p = d.dentry_parent {
            IncLink' = IncLink - i - p and 
            Alloc' = Alloc - i - d and 
            Start' = Start + p  and 
            Complete' = Complete + i
        }
    ) else (
        IncLink' = IncLink - i and 
        Alloc' = Alloc - i - d and 
        Complete' = Complete + i

    )
    Init' = Init + d
}

pred complete_creat [i: Inode, d: Dentry] {
    // guards
    i in Clean and d in Clean and
    i in Complete and d in Init

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    pm_states_unchanged
    unchanged[Free]
    unchanged[AllocStarted]
    unchanged[Alloc]
    unchanged[IncLink]

    // effects
    // i is moved from init to start
    Init' = Init - d
    Complete' = Complete - i
    Start' = Start + d + i
}

pred start_alloc_dir_page [p: DirPageHeader] {
    // guards 
    p in Clean and p in Free 

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    pm_states_unchanged
    start_alloc_frame_condition

    Free' = Free - p
    AllocStarted' = AllocStarted + p
}

pred set_page_header_type [p: PageHeader] {
    // guards 
    p in AllocStarted and p !in TypeSet

    // frame conditions
    pointers_unchanged 
    inode_values_unchanged
    op_states_unchanged 

    // effects
    make_dirty[p]
    TypeSet' = TypeSet + p
}

pred set_dir_page_parent [p: DirPageHeader, parent: DirInode] {
    // guards
    p in AllocStarted and no p.parent_ino

    // frame conditions
    all d: Dentry | unchanged[d.inode]
    all p: PageHeader | unchanged[p.inode]
    all p: DirPageHeader | unchanged[p.dentries]
    inode_values_unchanged
    op_states_unchanged

    // effects
    make_dirty[p]
    p.parent_ino' = p.parent_ino + parent
}

pred finish_alloc_dir_page [p: DirPageHeader] {
    // guards 
    p in AllocStarted and dir_page_header_alloc_done[p]

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    pm_states_unchanged
    finish_alloc_frame_condition

    AllocStarted' = AllocStarted - p
    Alloc' = Alloc + p
}

pred set_page_backpointer [p: PageHeader, i: Inode] {
    // guards 
    p in Alloc and no p.inode and p in Clean

    // frame conditions
    inode_values_unchanged
    all d: Dentry | unchanged[d.inode]
    all p: DirPageHeader | unchanged[p.dentries]
    all p0: PageHeader - p | (unchanged[p0.inode] and unchanged[p0.parent_ino])
    init_frame_condition

    // effects
    make_dirty[p]
    p.(inode') = p.inode + i // TODO: can we just set it to i?
    Alloc' = Alloc - p
    Init' = Init + p
}

pred inc_link_count [i: Inode] {
    // guards 
    i in Start 

    // frame conditions
    inode_values_unchanged_except[LcSet]
    op_states_unchanged_except[Start, IncLink]
    pointers_unchanged 
    all i0: Inode - i | unchanged[i0.link_count]

    // effects
    make_dirty[i]
    i.(link_count') = add[i.link_count, 1]
    Start' = Start - i 
    IncLink' = IncLink + i

}

pred clwb [o: PMObj] {
    // guard
    o in Dirty

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    unchanged[Clean]
    op_states_unchanged

    // effects
    InFlight' = InFlight + o
    Dirty' = Dirty - o
}

pred fence {
    // guard
    // not technically required for correctness, but it doesn't make senes
    // to call fence when there are no in-flight writes
    some InFlight

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    unchanged[Dirty]
    op_states_unchanged

    // effects
    // fence acts on ALL in-flight objects
    Clean' = Clean + InFlight
    no InFlight'
}

// skip essentially lets us terminate a trace at any point rather than
// having to set things up for a loop back to a previous state
// I don't think this will break anything because we are not interested
// in using `eventually`/proving liveness, but be careful
// TODO: is there a better way to do this?
pred skip {
    // no guard

    // frame conditions
    pointers_unchanged
    inode_values_unchanged
    pm_states_unchanged
    op_states_unchanged

    // no effects
}

